package org.lubei.bases.core.rpc;import org.lubei.bases.core.Server;import java.util.List;/** * rpc客户端通用接口 <p> rpc client interface * * @author hongliangpan@gmail.com */public interface IRpcClient {    <T> T getRemoteService(final RpcServerParams server, final Class<?> serviceClass);    <T> T getRemoteService(final Class<T> serviceClass);    <T> T getRemoteService(final String serverId, final Class<T> serviceClass);    <T> T getRemoteService(final String serverId, final Class<T> serviceInterface,                           final String... serviceClassName);    <T> void registerRemoteService(final Class<?> serviceClass, final T service);    <T> void unregisterRemoteService(final Class<?> serviceClass, final T service);    List<Server> getServers(Class clazz);    void stop();}